mapper.select:
SELECT 

FROM wx_location a

WHERE a.id = #{id}

SELECT 

FROM wx_location a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.isenable = #{isenable}




		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM wx_location a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE wx_location SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
